package Tests;import Settings.CapabilitiesSettings;import Utils.AppiumServer;import Utils.Helpers;import io.appium.java_client.AppiumDriver;import org.testng.annotations.AfterMethod;import org.testng.annotations.AfterSuite;import org.testng.annotations.BeforeSuite;import pages.ScreenBase;import java.io.FileInputStream;import java.util.Properties;public class TestBase {	public static AppiumDriver driver;	public static ScreenBase scr;	@BeforeSuite	public void setUp() throws Exception {		scr = new ScreenBase();		System.out.println("Starting Appium driver.....");		startDriver();	}	@AfterMethod	public void afterEachTest(){		driver.resetApp();	}	@AfterSuite	public void tearDown() throws Exception {		System.out.println(".......Stopping Appium driver");		driver.quit();		AppiumServer.stopAppiumServer();	}	public void startDriver() throws Exception {		AppiumServer.startAppiumServer("4723");		Properties prop = new Properties();		FileInputStream file = new FileInputStream(System.getProperty("user.dir") + "/src/test/resources/properties/platform.properties");		prop.load(file);		String platform = prop.getProperty("platform");		if (driver == null) {			if (platform.equals("iOS")) {				CapabilitiesSettings.setIOSCapabilities();				driver = CapabilitiesSettings.createIOSDriver();			}			if (platform.equals("Android")) {				CapabilitiesSettings.setAndroidCapabilities();				driver = CapabilitiesSettings.createAndroidDriver();			}			if (platform.equals("AndroidWeb")) {				CapabilitiesSettings.setAndroidWEBCapabilities();				driver = CapabilitiesSettings.createAndroidDriver();			}			Helpers.init(driver);			scr.initialize();		}		else {			driver.resetApp();		}	}}